// src/components/Pages/Produtos.js
import React, { useState, useEffect } from 'react';

const Produtos = () => {
  const [produtos, setProdutos] = useState([]);
  const [produtoEditando, setProdutoEditando] = useState(null);
  const [modoEdicao, setModoEdicao] = useState(false);
  const [filtroCategoria, setFiltroCategoria] = useState('Todas');
  const [busca, setBusca] = useState('');

  // Estado do formul√°rio
  const [formData, setFormData] = useState({
    nome: '',
    categoria: 'Bebida',
    preco: '',
    estoque: '',
    usaEstoqueMinimo: false,
    estoqueMinimo: ''
  });

  // Mock inicial de produtos
  const produtosIniciais = [
    { id: 1, nome: 'Caf√© Expresso', categoria: 'Bebida', preco: 3.50, estoque: 45, estoqueMinimo: 10 },
    { id: 2, nome: 'Cappuccino', categoria: 'Bebida', preco: 5.00, estoque: 38, estoqueMinimo: 15 },
    { id: 3, nome: 'P√£o de Queijo', categoria: 'Alimento', preco: 4.00, estoque: 62, estoqueMinimo: 20 },
    { id: 4, nome: 'Croissant', categoria: 'Alimento', preco: 6.50, estoque: 28, estoqueMinimo: 10 },
    { id: 5, nome: 'Suco Natural', categoria: 'Bebida', preco: 7.00, estoque: 40, estoqueMinimo: 15 },
    { id: 6, nome: 'Bolo', categoria: 'Sobremesa', preco: 8.00, estoque: 15, estoqueMinimo: 5 },
    { id: 7, nome: '√Ågua Mineral', categoria: 'Bebida', preco: 3.00, estoque: 50, estoqueMinimo: null }
  ];

  useEffect(() => {
    // Carregar produtos do localStorage ou usar mock inicial
    const produtosSalvos = localStorage.getItem('cafeteria_produtos');
    if (produtosSalvos) {
      setProdutos(JSON.parse(produtosSalvos));
    } else {
      setProdutos(produtosIniciais);
      localStorage.setItem('cafeteria_produtos', JSON.stringify(produtosIniciais));
    }
  }, []);

  // Salvar produtos no localStorage sempre que houver altera√ß√£o
  useEffect(() => {
    localStorage.setItem('cafeteria_produtos', JSON.stringify(produtos));
  }, [produtos]);

  // Categorias √∫nicas para filtro
  const categorias = ['Todas', ...new Set(produtos.map(p => p.categoria))];

  // Filtrar produtos
  const produtosFiltrados = produtos.filter(produto => {
    const passaCategoria = filtroCategoria === 'Todas' || produto.categoria === filtroCategoria;
    const passaBusca = produto.nome.toLowerCase().includes(busca.toLowerCase());
    return passaCategoria && passaBusca;
  });

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const validarFormulario = () => {
    if (!formData.nome.trim()) {
      alert('Nome do produto √© obrigat√≥rio!');
      return false;
    }
    if (!formData.preco || parseFloat(formData.preco) <= 0) {
      alert('Pre√ßo deve ser maior que zero!');
      return false;
    }
    if (!formData.estoque || parseInt(formData.estoque) < 0) {
      alert('Estoque n√£o pode ser negativo!');
      return false;
    }
    if (formData.usaEstoqueMinimo && (!formData.estoqueMinimo || parseInt(formData.estoqueMinimo) < 0)) {
      alert('Estoque m√≠nimo √© obrigat√≥rio quando marcado!');
      return false;
    }
    return true;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!validarFormulario()) return;

    const novoProduto = {
      id: modoEdicao ? produtoEditando.id : Date.now(),
      nome: formData.nome.trim(),
      categoria: formData.categoria,
      preco: parseFloat(formData.preco),
      estoque: parseInt(formData.estoque),
      estoqueMinimo: formData.usaEstoqueMinimo ? parseInt(formData.estoqueMinimo) : null
    };

    if (modoEdicao) {
      // Editar produto existente
      setProdutos(produtos.map(p => p.id === produtoEditando.id ? novoProduto : p));
      alert('Produto atualizado com sucesso!');
    } else {
      // Adicionar novo produto
      setProdutos([...produtos, novoProduto]);
      alert('Produto cadastrado com sucesso!');
    }

    // Limpar formul√°rio
    setFormData({
      nome: '',
      categoria: 'Bebida',
      preco: '',
      estoque: '',
      usaEstoqueMinimo: false,
      estoqueMinimo: ''
    });
    setModoEdicao(false);
    setProdutoEditando(null);
  };

  const editarProduto = (produto) => {
    setFormData({
      nome: produto.nome,
      categoria: produto.categoria,
      preco: produto.preco.toString(),
      estoque: produto.estoque.toString(),
      usaEstoqueMinimo: produto.estoqueMinimo !== null,
      estoqueMinimo: produto.estoqueMinimo !== null ? produto.estoqueMinimo.toString() : ''
    });
    setProdutoEditando(produto);
    setModoEdicao(true);
  };

  const excluirProduto = (id) => {
    if (window.confirm('Tem certeza que deseja excluir este produto?')) {
      setProdutos(produtos.filter(p => p.id !== id));
      alert('Produto exclu√≠do com sucesso!');
    }
  };

  const cancelarEdicao = () => {
    setFormData({
      nome: '',
      categoria: 'Bebida',
      preco: '',
      estoque: '',
      usaEstoqueMinimo: false,
      estoqueMinimo: ''
    });
    setModoEdicao(false);
    setProdutoEditando(null);
  };

  const getStatusEstoque = (produto) => {
    if (produto.estoqueMinimo !== null && produto.estoque <= produto.estoqueMinimo) {
      return 'danger'; // Estoque cr√≠tico
    }
    if (produto.estoque === 0) {
      return 'secondary'; // Esgotado
    }
    return 'success'; // Normal
  };

  return (
    <div className="container-fluid">
      <h2>Gerenciar Produtos</h2>

      {/* Filtros e Busca */}
      <div className="card mb-4">
        <div className="card-header">
          <h5>Filtros</h5>
        </div>
        <div className="card-body">
          <div className="row">
            <div className="col-md-4">
              <label className="form-label">Categoria</label>
              <select
                className="form-select"
                value={filtroCategoria}
                onChange={(e) => setFiltroCategoria(e.target.value)}
              >
                {categorias.map(cat => (
                  <option key={cat} value={cat}>{cat}</option>
                ))}
              </select>
            </div>
            <div className="col-md-4">
              <label className="form-label">Buscar Produto</label>
              <input
                type="text"
                className="form-control"
                placeholder="Digite o nome do produto..."
                value={busca}
                onChange={(e) => setBusca(e.target.value)}
              />
            </div>
            <div className="col-md-4">
              <label className="form-label">Total de Produtos</label>
              <div className="form-control bg-light">
                <strong>{produtosFiltrados.length}</strong> produtos encontrados
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="row">
        {/* Formul√°rio */}
        <div className="col-md-4">
          <div className="card">
            <div className="card-header">
              <h5>{modoEdicao ? 'Editar Produto' : 'Cadastrar Novo Produto'}</h5>
            </div>
            <div className="card-body">
              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label className="form-label">Nome do Produto *</label>
                  <input
                    type="text"
                    className="form-control"
                    name="nome"
                    value={formData.nome}
                    onChange={handleInputChange}
                    required
                  />
                </div>

                <div className="mb-3">
                  <label className="form-label">Categoria *</label>
                  <select
                    className="form-select"
                    name="categoria"
                    value={formData.categoria}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="Bebida">Bebida</option>
                    <option value="Alimento">Alimento</option>
                    <option value="Sobremesa">Sobremesa</option>
                    <option value="Outros">Outros</option>
                  </select>
                </div>

                <div className="mb-3">
                  <label className="form-label">Pre√ßo (R$) *</label>
                  <input
                    type="number"
                    step="0.01"
                    min="0.01"
                    className="form-control"
                    name="preco"
                    value={formData.preco}
                    onChange={handleInputChange}
                    required
                  />
                </div>

                <div className="mb-3">
                  <label className="form-label">Estoque Atual *</label>
                  <input
                    type="number"
                    min="0"
                    className="form-control"
                    name="estoque"
                    value={formData.estoque}
                    onChange={handleInputChange}
                    required
                  />
                </div>

                <div className="mb-3 form-check">
                  <input
                    type="checkbox"
                    className="form-check-input"
                    name="usaEstoqueMinimo"
                    checked={formData.usaEstoqueMinimo}
                    onChange={handleInputChange}
                  />
                  <label className="form-check-label">Usar Estoque M√≠nimo</label>
                </div>

                {formData.usaEstoqueMinimo && (
                  <div className="mb-3">
                    <label className="form-label">Estoque M√≠nimo *</label>
                    <input
                      type="number"
                      min="0"
                      className="form-control"
                      name="estoqueMinimo"
                      value={formData.estoqueMinimo}
                      onChange={handleInputChange}
                      required={formData.usaEstoqueMinimo}
                    />
                  </div>
                )}

                <div className="d-grid gap-2">
                  <button type="submit" className="btn btn-primary">
                    {modoEdicao ? 'Atualizar Produto' : 'Cadastrar Produto'}
                  </button>
                  {modoEdicao && (
                    <button type="button" className="btn btn-secondary" onClick={cancelarEdicao}>
                      Cancelar Edi√ß√£o
                    </button>
                  )}
                </div>
              </form>
            </div>
          </div>
        </div>

        {/* Tabela de Produtos */}
        <div className="col-md-8">
          <div className="card">
            <div className="card-header">
              <h5>Lista de Produtos</h5>
            </div>
            <div className="card-body">
              <div className="table-responsive">
                <table className="table table-striped table-hover">
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>Nome</th>
                      <th>Categoria</th>
                      <th>Pre√ßo</th>
                      <th>Estoque</th>
                      <th>Estoque M√≠n.</th>
                      <th>Status</th>
                      <th>A√ß√µes</th>
                    </tr>
                  </thead>
                  <tbody>
                    {produtosFiltrados.map(produto => (
                      <tr key={produto.id} className={`table-${getStatusEstoque(produto)}`}>
                        <td>{produto.id}</td>
                        <td>
                          <strong>{produto.nome}</strong>
                          {getStatusEstoque(produto) === 'danger' && (
                            <span className="badge bg-danger ms-2">Baixo Estoque</span>
                          )}
                          {getStatusEstoque(produto) === 'secondary' && (
                            <span className="badge bg-secondary ms-2">Esgotado</span>
                          )}
                        </td>
                        <td>
                          <span className="badge bg-info">{produto.categoria}</span>
                        </td>
                        <td>R$ {produto.preco.toFixed(2)}</td>
                        <td>
                          <span className={`badge bg-${getStatusEstoque(produto)}`}>
                            {produto.estoque}
                          </span>
                        </td>
                        <td>
                          {produto.estoqueMinimo !== null ? (
                            <span className="badge bg-warning">{produto.estoqueMinimo}</span>
                          ) : (
                            <span className="text-muted">-</span>
                          )}
                        </td>
                        <td>
                          {getStatusEstoque(produto) === 'danger' && '‚õî Cr√≠tico'}
                          {getStatusEstoque(produto) === 'secondary' && '‚ùå Esgotado'}
                          {getStatusEstoque(produto) === 'success' && '‚úÖ Normal'}
                        </td>
                        <td>
                          <div className="btn-group btn-group-sm">
                            <button
                              className="btn btn-outline-primary"
                              onClick={() => editarProduto(produto)}
                              title="Editar"
                            >
                              ‚úèÔ∏è
                            </button>
                            <button
                              className="btn btn-outline-danger"
                              onClick={() => excluirProduto(produto.id)}
                              title="Excluir"
                            >
                              üóëÔ∏è
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>

              {produtosFiltrados.length === 0 && (
                <div className="text-center text-muted py-4">
                  <p>Nenhum produto encontrado.</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Produtos;
