// src/components/Pages/Movimentacoes.js
import React, { useState, useEffect } from 'react';

const Movimentacoes = () => {
  const [movimentacoes, setMovimentacoes] = useState([]);
  const [produtos, setProdutos] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [filtros, setFiltros] = useState({
    cafeteria: 'Todas',
    tipo: 'Todos',
    dataInicio: new Date().toISOString().split('T')[0],
    dataFim: new Date().toISOString().split('T')[0]
  });
  const [novaMovimentacao, setNovaMovimentacao] = useState({
    produtoId: '',
    tipo: 'entrada',
    quantidade: '',
    motivo: '',
    notaFiscal: '',
    cafeteria: 'Cafeteria Principal'
  });

  // Tipos de movimenta√ß√£o
  const tiposMovimentacao = [
    { value: 'entrada', label: 'Entrada - Nota Fiscal', icon: 'üì•' },
    { value: 'ajuste_entrada', label: 'Ajuste - Entrada', icon: 'üìà' },
    { value: 'ajuste_saida', label: 'Ajuste - Sa√≠da', icon: 'üìâ' },
    { value: 'perda', label: 'Perda/Avaria', icon: '‚ö†Ô∏è' },
    { value: 'venda', label: 'Sa√≠da - Venda', icon: 'üí∞' }
  ];

  // Buscar produtos do backend
  useEffect(() => {
    const fetchProdutos = async () => {
      try {
        setLoading(true);
        const response = await fetch('http://localhost:5001/api/products');
        
        if (!response.ok) {
          throw new Error('Erro ao carregar produtos');
        }
        
        const produtosData = await response.json();
        console.log('üì¶ Produtos carregados do backend:', produtosData);
        setProdutos(produtosData);
      } catch (error) {
        console.error('Erro ao buscar produtos:', error);
        setError('Erro ao carregar produtos do servidor');
      } finally {
        setLoading(false);
      }
    };

    fetchProdutos();
  }, []);

  // Buscar movimenta√ß√µes do backend
  useEffect(() => {
    const fetchMovimentacoes = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/movimentacoes');
        
        if (response.ok) {
          const movimentacoesData = await response.json();
          console.log('üì¶ Movimenta√ß√µes carregadas do backend:', movimentacoesData);
          setMovimentacoes(movimentacoesData);
        } else {
          // Se n√£o encontrar movimenta√ß√µes no backend, carrega do localStorage
          const movimentacoesSalvas = localStorage.getItem('cafeteria_movimentacoes');
          if (movimentacoesSalvas) {
            setMovimentacoes(JSON.parse(movimentacoesSalvas));
          }
        }
      } catch (error) {
        console.error('Erro ao buscar movimenta√ß√µes:', error);
        // Fallback para localStorage
        const movimentacoesSalvas = localStorage.getItem('cafeteria_movimentacoes');
        if (movimentacoesSalvas) {
          setMovimentacoes(JSON.parse(movimentacoesSalvas));
        }
      }
    };

    fetchMovimentacoes();
  }, []);

  // Salvar movimenta√ß√µes no localStorage como backup
  useEffect(() => {
    localStorage.setItem('cafeteria_movimentacoes', JSON.stringify(movimentacoes));
  }, [movimentacoes]);

  // Filtrar movimenta√ß√µes
  const movimentacoesFiltradas = movimentacoes.filter(mov => {
    const dataMov = new Date(mov.dataHora);
    const dataInicio = new Date(filtros.dataInicio);
    const dataFim = new Date(filtros.dataFim);
    dataFim.setHours(23, 59, 59, 999);

    const passaCafeteria = filtros.cafeteria === 'Todas' || mov.cafeteria === filtros.cafeteria;
    const passaTipo = filtros.tipo === 'Todos' || mov.tipo === filtros.tipo;
    const passaData = dataMov >= dataInicio && dataMov <= dataFim;

    return passaCafeteria && passaTipo && passaData;
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNovaMovimentacao(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFiltroChange = (e) => {
    const { name, value } = e.target;
    setFiltros(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const getSinalQuantidade = (tipo) => {
    if (['entrada', 'ajuste_entrada'].includes(tipo)) return '+';
    if (['ajuste_saida', 'perda', 'venda'].includes(tipo)) return '-';
    return '';
  };

  const getTipoLabel = (tipo) => {
    const tipoObj = tiposMovimentacao.find(t => t.value === tipo);
    return tipoObj ? `${tipoObj.icon} ${tipoObj.label}` : tipo;
  };

  const validarMovimentacao = () => {
    if (!novaMovimentacao.produtoId) {
      alert('Selecione um produto!');
      return false;
    }
    if (!novaMovimentacao.quantidade || parseInt(novaMovimentacao.quantidade) <= 0) {
      alert('Quantidade deve ser maior que zero!');
      return false;
    }
    if (novaMovimentacao.tipo === 'entrada' && !novaMovimentacao.notaFiscal) {
      alert('Nota fiscal √© obrigat√≥ria para entrada!');
      return false;
    }
    if (['ajuste_saida', 'perda', 'venda'].includes(novaMovimentacao.tipo) && !novaMovimentacao.motivo) {
      alert('Motivo √© obrigat√≥rio para sa√≠das!');
      return false;
    }
    return true;
  };

  const enviarMovimentacaoParaBackend = async (movimentacao) => {
    try {
      const response = await fetch('http://localhost:5001/api/movimentacoes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(movimentacao)
      });

      if (!response.ok) {
        throw new Error('Erro ao salvar movimenta√ß√£o no servidor');
      }

      return await response.json();
    } catch (error) {
      console.error('Erro ao enviar movimenta√ß√£o para o backend:', error);
      throw error;
    }
  };

  const atualizarEstoqueNoBackend = async (produtoId, quantidade, tipo) => {
    try {
      const response = await fetch(`http://localhost:5001/api/products/${produtoId}/estoque`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          operacao: tipo.includes('entrada') ? 'entrada' : 'saida',
          quantidade: parseInt(quantidade)
        })
      });

      if (!response.ok) {
        throw new Error('Erro ao atualizar estoque no servidor');
      }

      return await response.json();
    } catch (error) {
      console.error('Erro ao atualizar estoque no backend:', error);
      throw error;
    }
  };

  const registrarMovimentacao = async (e) => {
    e.preventDefault();
    
    if (!validarMovimentacao()) return;

    const produto = produtos.find(p => p._id === novaMovimentacao.produtoId);
    if (!produto) {
      alert('Produto n√£o encontrado!');
      return;
    }

    if (['ajuste_saida', 'perda', 'venda'].includes(novaMovimentacao.tipo)) {
      if (produto.estoque < parseInt(novaMovimentacao.quantidade)) {
        alert(`Estoque insuficiente! Dispon√≠vel: ${produto.estoque}`);
        return;
      }
    }

    const novaMov = {
      produtoId: novaMovimentacao.produtoId,
      produtoNome: produto.nome,
      tipo: novaMovimentacao.tipo,
      quantidade: parseInt(novaMovimentacao.quantidade),
      motivo: novaMovimentacao.motivo,
      notaFiscal: novaMovimentacao.notaFiscal,
      usuario: 'admin',
      cafeteria: novaMovimentacao.cafeteria,
      estoqueAnterior: produto.estoque,
      estoqueAtual: calcularNovoEstoque(produto.estoque, novaMovimentacao)
    };

    try {
      // 1. Primeiro atualiza o estoque no backend
      const produtoAtualizado = await atualizarEstoqueNoBackend(
        novaMovimentacao.produtoId,
        novaMovimentacao.quantidade,
        novaMovimentacao.tipo
      );

      // 2. Depois envia a movimenta√ß√£o para o backend
      const movimentacaoSalva = await enviarMovimentacaoParaBackend(novaMov);

      // 3. Atualiza o estado local com os dados do backend
      setMovimentacoes([movimentacaoSalva, ...movimentacoes]);
      
      // 4. Atualiza a lista de produtos com o novo estoque
      setProdutos(produtos.map(p => 
        p._id === produtoAtualizado._id ? produtoAtualizado : p
      ));

      setNovaMovimentacao({
        produtoId: '',
        tipo: 'entrada',
        quantidade: '',
        motivo: '',
        notaFiscal: '',
        cafeteria: 'Cafeteria Principal'
      });

      alert('Movimenta√ß√£o registrada com sucesso no banco de dados!');
    } catch (error) {
      console.error('Erro ao registrar movimenta√ß√£o:', error);
      alert('Erro ao registrar movimenta√ß√£o no banco de dados. Verifique o console para mais detalhes.');
    }
  };

  const calcularNovoEstoque = (estoqueAtual, movimentacao) => {
    const quantidade = parseInt(movimentacao.quantidade);
    if (['entrada', 'ajuste_entrada'].includes(movimentacao.tipo)) {
      return estoqueAtual + quantidade;
    } else {
      return Math.max(0, estoqueAtual - quantidade);
    }
  };

  const formatarDataHora = (dataHora) => {
    return new Date(dataHora).toLocaleString('pt-BR');
  };

  if (loading) {
    return (
      <div className="container-fluid">
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Carregando...</span>
          </div>
          <p className="mt-3">Carregando produtos...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container-fluid">
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid">
      <h2>Movimenta√ß√µes de Estoque</h2>

      {/* Resto do c√≥digo permanece igual */}
      {/* ... */}
    </div>
  );
};

export default Movimentacoes;
